import "./PageAboutMe.css";
import React, { useState } from "react";
import ChangeLogo from "./todo-components/ChangeLogo";

const PageAboutMe = ({ image, name, cv, setLogoButton, logoButton }) => {
  const [message, setMessage] = useState("");
  const [isSent, setIsSent] = useState(false);
  const [logoData, setLogoData] = useState("");

  const messageHandler = (event) => {
    setMessage(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    setIsSent(true);
    setLogoData(message);
    setMessage("");
  };
  return (
    <div className="body">
      <img src={image} alt="" className="image" />
      <div className="data-container">
        <div className="button-53">{name}</div>
        <div className="text">{cv}</div>
      </div>
      <form onSubmit={submitHandler}>
        <div className="button-53">WRITE MESSAGE INSIDE LOGO</div>
        <label>message</label>
        <input type="text" value={message} onChange={messageHandler}></input>
        <button className="button-53" type="submit">
          sent
        </button>
      </form>
      {isSent ? "wyslane" : "nie wyslane"}
      <ChangeLogo setLogoButton={setLogoButton} logoButton={logoButton} />
    </div>
  );
};
export default PageAboutMe;
