    state.position = event.target.innerHTML;
    console.log("HEJ", state, event.target.innerHTML);
  };

  const onButtonClick = (mode) => {
    setSelectedButton(mode);
    state.position = mode.target.innerHTML;
    console.log("HEJ", state, mode.target.innerHTML);
  };

  return (
    <button className="button-53" onClick={() => onButtonClick({ text })}>
      {text}
    </button>
  );
};

export default NavButton;
